'use client'

import { Button } from '@/registry/default/ui/button'
import { cn } from '@/lib/utils'
import {
    Popover,
    PopoverContent,
    PopoverTrigger,
} from '@/registry/default/ui/popover'
import {
    Command,
    CommandEmpty,
    CommandGroup,
    CommandInput,
    CommandItem,
    CommandList,
    CommandSeparator,
} from '@/registry/default/ui/command'
import { ChevronsUpDownIcon, CheckIcon, PlusCircleIcon } from 'lucide-react'
import React from 'react'

const defaultTeams = [
    { id: 'alifarooq', name: "Ali Farooq's Team" },
    { id: 'shadcn', name: "Shad CN's Team" },
    { id: 'leerob', name: "Lee Robinson's Team" },
]

export function TeamSwitcher() {
    const [team, setTeam] = React.useState(defaultTeams[0])
    const [open, setOpen] = React.useState(false)

    const onCreateNewTeam = () => {
        // Logic to create a new team can be added here
    }

    return (
        <Popover open={open} onOpenChange={setOpen}>
            <PopoverTrigger asChild>
                <Button
                    variant="ghost"
                    size="sm"
                    role="combobox"
                    aria-expanded={open}
                    className={cn(
                        "dark:hover:bg-accent max-w-[140px] cursor-pointer justify-start px-2 has-[>svg]:px-2 sm:max-w-[180px] [&_svg:not([class*='size-'])]:size-3"
                    )}
                >
                    <div className="size-5 shrink-0 rounded-full bg-white" />
                    <p className="truncate">{team.name}</p>
                    <ChevronsUpDownIcon />
                </Button>
            </PopoverTrigger>
            <PopoverContent className="p-0" align="start">
                <Command>
                    <CommandInput placeholder="Search team..." />
                    <CommandList>
                        <CommandEmpty>No team found.</CommandEmpty>
                        <CommandGroup className="max-h-60 overflow-y-auto">
                            {defaultTeams.map((teamOption) => (
                                <CommandItem
                                    key={teamOption.id}
                                    value={teamOption.id}
                                    onSelect={(currentValue) => {
                                        setTeam(
                                            defaultTeams.find(
                                                (t) => t.id === currentValue
                                            ) || defaultTeams[0]
                                        )
                                        setOpen(false)
                                    }}
                                    className="flex items-center gap-3"
                                >
                                    <div className="size-5 rounded-full bg-white" />
                                    {teamOption.name}
                                    <CheckIcon
                                        className={cn(
                                            'ml-auto h-4 w-4',
                                            team.id === teamOption.id
                                                ? 'opacity-100'
                                                : 'opacity-0'
                                        )}
                                    />
                                </CommandItem>
                            ))}
                        </CommandGroup>
                        <CommandSeparator />
                        <CommandGroup>
                            <CommandItem onSelect={onCreateNewTeam}>
                                <PlusCircleIcon />
                                Create new team
                            </CommandItem>
                        </CommandGroup>
                    </CommandList>
                </Command>
            </PopoverContent>
        </Popover>
    )
}

'use client'

import { Button } from '@/registry/default/ui/button'
import { cn } from '@/lib/utils'
import { useIsMac } from '@/registry/default/hooks/use-is-mac'

export function Search({ className }: React.ComponentProps<'button'>) {
    const isMac = useIsMac()

    return (
        <Button
            variant="secondary"
            className={cn(
                'bg-surface text-surface-foreground/60 dark:bg-card relative h-8 w-full justify-start pl-2.5 font-normal shadow-none sm:pr-12 md:w-40 lg:w-56 xl:w-64',
                className
            )}
        >
            <span className="hidden lg:inline-flex">Search...</span>
            <span className="inline-flex lg:hidden">Search...</span>
            <div className="absolute top-1.5 right-1.5 hidden gap-1 sm:flex">
                <CommandMenuKbd>{isMac ? 'âŒ˜' : 'Ctrl'}</CommandMenuKbd>
                <CommandMenuKbd className="aspect-square">K</CommandMenuKbd>
            </div>
        </Button>
    )
}

function CommandMenuKbd({ className, ...props }: React.ComponentProps<'kbd'>) {
    return (
        <kbd
            className={cn(
                "bg-background text-muted-foreground pointer-events-none flex h-5 items-center justify-center gap-1 rounded border px-1 font-sans text-[0.7rem] font-medium select-none [&_svg:not([class*='size-'])]:size-3",
                className
            )}
            {...props}
        />
    )
}

'use client'

import React from 'react'
import {
    Popover,
    PopoverContent,
    PopoverTrigger,
} from '@/registry/default/ui/popover'
import { Button } from '@/registry/default/ui/button'
import { cn } from '@/lib/utils'
// import Link from 'next/link'

type MobileNavProps = {
    nav: {
        name: string
        items: {
            label: string
            href: string
        }[]
    }[]
}

export function MobileNav({ nav }: MobileNavProps) {
    const Link = 'a' // if using Next.js remove this line. you can use the Link component from 'next/link'

    const [open, setOpen] = React.useState(false)

    return (
        <Popover open={open} onOpenChange={setOpen} modal={true}>
            <PopoverTrigger asChild>
                <Button
                    variant="ghost"
                    size="icon"
                    className={cn(
                        'extend-touch-target block size-8 touch-manipulation items-center justify-start gap-2.5 hover:bg-transparent focus-visible:bg-transparent focus-visible:ring-0 active:bg-transparent md:hidden dark:hover:bg-transparent'
                    )}
                >
                    <div className="relative flex items-center justify-center">
                        <div className="relative size-4">
                            <span
                                className={cn(
                                    'bg-foreground absolute left-0 block h-0.5 w-4 transition-all duration-100',
                                    open ? 'top-[0.4rem] -rotate-45' : 'top-1'
                                )}
                            />
                            <span
                                className={cn(
                                    'bg-foreground absolute left-0 block h-0.5 w-4 transition-all duration-100',
                                    open ? 'top-[0.4rem] rotate-45' : 'top-2.5'
                                )}
                            />
                        </div>
                        <span className="sr-only">Toggle Menu</span>
                    </div>
                </Button>
            </PopoverTrigger>
            <PopoverContent
                className="bg-background/90 no-scrollbar h-(--radix-popover-content-available-height) w-(--radix-popover-content-available-width) overflow-y-auto rounded-none border-none p-0 shadow-none backdrop-blur duration-100"
                align="start"
                side="bottom"
                alignOffset={-16}
                sideOffset={4}
            >
                <div className="flex flex-col gap-12 overflow-auto px-6 py-6">
                    {nav.map((category, index) => (
                        <div className="flex flex-col gap-4" key={index}>
                            <p className="text-muted-foreground text-sm font-medium">
                                {category.name}
                            </p>
                            <div className="flex flex-col gap-3">
                                {category.items.map((item, index) => (
                                    <Link
                                        key={index}
                                        href={item.href as string}
                                        className="text-2xl font-medium"
                                        onClick={() => setOpen(false)}
                                    >
                                        {item.label}
                                    </Link>
                                ))}
                            </div>
                        </div>
                    ))}
                </div>
            </PopoverContent>
        </Popover>
    )
}
